/**
 * Generic entity-related functions. These functions will (eventually) be added to the game engine API
 */
/* globals ENTITY_ENGINE */

module.exports = (function () {
	return {
		isPlayer : isPlayer,
		isNpc : isNpc,
		getName : getName,
		say : say
	};
	
	function isPlayer (entity) {
		return ENTITY_ENGINE.isPlayer(entity);
	}
	
	function isNpc (entity) {
		return ENTITY_ENGINE.isNpc(entity);
	}
	
	function getName(entity) {
		return ENTITY_ENGINE.getName(entity);
	}
	
	function say (entity, message) {
		ENTITY_ENGINE.say(entity, message);
	}
})();